package tcs.ril.storebot.view.Activity;import android.support.design.widget.TabLayout;import android.support.v4.view.ViewPager;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.ImageView;import java.util.ArrayList;import java.util.List;import tcs.ril.storebot.R;import tcs.ril.storebot.model.HelpPagerData;import tcs.ril.storebot.view.Adapter.ViewPagerAdapter;public class HelpActivity extends AppCompatActivity {     ImageView back;     ViewPager helpPager;     List<HelpPagerData> helpPagerData;     TabLayout tabLayout;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_help);        helpPager=findViewById(R.id.help_pager);        back=findViewById(R.id.help_back);        helpPagerData=new ArrayList<>();        helpPagerData=createData();        tabLayout=findViewById(R.id.tab);        back.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                finish();            }        });            helpPager.setClipToPadding(false);            helpPager.setPageMargin(12);        helpPager.setAdapter(new ViewPagerAdapter(this,helpPagerData));        tabLayout.setupWithViewPager(helpPager);    }    @Override    public void onBackPressed() {        finish();    }    public List<HelpPagerData> createData()    {        List<HelpPagerData> helpList=new ArrayList<>();        List<String> customerPoints=new ArrayList<>();        customerPoints.add("Show me Customer Details");        customerPoints.add("Show me details of the \"user\"");        customerPoints.add("Personalised Deal for \"user\"");        customerPoints.add("Current order of \"user\"");        customerPoints.add("Past order of \"user\"");        List<String> productPoints=new ArrayList<>();        productPoints.add("Show me \"Product\" Details");        productPoints.add("Palnogram of Aisle \"number\"");        productPoints.add("Similar Products");        productPoints.add("Store Deals");        productPoints.add("offer on Product");        List<String>  orderPoints=new ArrayList<>();        orderPoints.add("Show me my picklist");        orderPoints.add("Critical Products");        orderPoints.add("Pending Orders");        orderPoints.add("Product Refund");        orderPoints.add("Product Reorder");        orderPoints.add("Critical Return");        HelpPagerData customerAssistance=new HelpPagerData("Customer Assistance",customerPoints);        HelpPagerData productManagement=new HelpPagerData("Product Management",productPoints);        HelpPagerData Orders=new HelpPagerData("Orders and Returns",orderPoints);        helpList.add(customerAssistance);        helpList.add(productManagement);        helpList.add(Orders);        return helpList;    }}