package tcs.ril.storebot.view.Activity;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.text.method.ScrollingMovementMethod;import android.util.Log;import android.view.View;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.TextView;import com.squareup.picasso.Picasso;import tcs.ril.storebot.R;import static tcs.ril.storebot.view.Activity.MainActivity.url;public class PendingDescriptionActivity extends AppCompatActivity {    private static final String LOGTAG ="Pending Description : " ;    TextView descText;    ImageView descImage;    LinearLayout scroll;    LinearLayout backBtn;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_pending_description);        descText=findViewById(R.id.details_text);        descImage=findViewById(R.id.details_image);        backBtn=findViewById(R.id.back_btn);        scroll=findViewById(R.id.scroll_layout);        String description= getIntent().getStringExtra("DESC");        descText.setText(description);        descText.setMovementMethod(new ScrollingMovementMethod());        descImage.setVisibility(View.GONE);    backBtn.setOnClickListener(new View.OnClickListener() {        @Override        public void onClick(View view) {            finish();        }    });    }}