package tcs.ril.storebot.view.Activity;import android.content.Intent;import android.os.Handler;import android.os.Message;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.util.Log;import android.view.Window;import android.view.WindowManager;import tcs.ril.storebot.R;import tcs.ril.storebot.model.PreferenceManager;public class SplashScreenActivity extends AppCompatActivity {    private static  final int STOPSPLASH=0;    private static  final int SPLASHTIME=2000;    private static final String TAG ="Splash" ;    private PreferenceManager manager=new PreferenceManager(SplashScreenActivity.this);    private Handler splashHandler=new Handler(){        @Override        public void handleMessage(Message msg){            switch (msg.what){                case STOPSPLASH:                    if(manager.getAccessToken(PreferenceManager.ACCESS_TOKEN).equals("LOGIN")==true)                    {                    Intent intent=new Intent(SplashScreenActivity.this,LoginActivity.class);                    finish();                    startActivity(intent);}                    else{                        Intent intent=new Intent(SplashScreenActivity.this,MainActivity.class);                        finish();                        startActivity(intent);                    }                    break;            }            super.handleMessage(msg);        }    };    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_splash_screen);        this.getWindow().setFlags(WindowManager.LayoutParams.FLAG_FULLSCREEN, WindowManager.LayoutParams.FLAG_FULLSCREEN);        this.setContentView(R.layout.activity_splash_screen);        Message msg=new Message();        msg.what=STOPSPLASH;        Log.d(TAG, "handleMessage: "+manager.getAccessToken(PreferenceManager.ACCESS_TOKEN));        splashHandler.sendMessageDelayed(msg,SPLASHTIME);    }}