package tcs.ril.storebot.view.Adapter;import android.app.Activity;import android.content.Context;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import java.util.List;import tcs.ril.storebot.R;import tcs.ril.storebot.model.HelpPagerData;public class HelpPagerBulletPointsAdpater extends RecyclerView.Adapter<HelpPagerBulletPointsAdpater.BulletViewHolder> {    Context context;    List<String> bulletPoints;    public static class BulletViewHolder extends RecyclerView.ViewHolder{        TextView bulletText;        public BulletViewHolder(@NonNull View itemView) {            super(itemView);            bulletText = itemView.findViewById(R.id.bullet_text);        }    }    public HelpPagerBulletPointsAdpater(Context mContext, HelpPagerData helpPagerData) {        this.context=mContext;        this.bulletPoints=helpPagerData.getBulletPoints();    }    @NonNull    @Override    public HelpPagerBulletPointsAdpater.BulletViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int position) {        View view=LayoutInflater.from(context).inflate(R.layout.bullet_points_item,parent,false);   return new BulletViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull BulletViewHolder bulletViewHolder, int i) {        bulletViewHolder.bulletText.setText(bulletPoints.get(i));    }    @Override    public int getItemCount() {       return  bulletPoints.size();    }}