package tcs.ril.storebot.view.Adapter;import android.app.Activity;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import java.util.List;import tcs.ril.storebot.R;public class PendingProductAdapater extends RecyclerView.Adapter<PendingProductAdapater.PendingProductViewHolder> {    List<String> PendingProducts;    Activity mContext;    View view;    public PendingProductAdapater(List<String> pendingProducts, Activity mContext) {        PendingProducts = pendingProducts;        this.mContext = mContext;    }    @NonNull    @Override    public PendingProductViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int position) {        view= LayoutInflater.from(parent.getContext()).inflate(R.layout.pickup_product_item,null,false);        return new PendingProductViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull PendingProductViewHolder holder, int position) {            holder.productName.setText(PendingProducts.get(position));    }    @Override    public int getItemCount() {        return PendingProducts.size();    }    public class PendingProductViewHolder extends RecyclerView.ViewHolder{        TextView productName;        public PendingProductViewHolder(@NonNull View itemView) {            super(itemView);            productName=itemView.findViewById(R.id.pick_product_text);        }    }}