package tcs.ril.storebot.view.Adapter;import android.app.Activity;import android.os.Build;import android.speech.tts.TextToSpeech;import android.speech.tts.UtteranceProgressListener;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.util.Log;import android.view.View;import android.widget.LinearLayout;import android.widget.TextView;import java.util.ArrayList;import java.util.List;import java.util.Locale;import tcs.ril.storebot.R;import tcs.ril.storebot.model.UserMessage;import static android.support.constraint.Constraints.TAG;//import static tcs.ril.storebot.view.Adapter.ListButtonAdapter.buttons;import static tcs.ril.storebot.view.Activity.MainActivity.mes;import static tcs.ril.storebot.view.Activity.MainActivity.t1;import static tcs.ril.storebot.view.Adapter.ListButtonAdapter.buttons;import static tcs.ril.storebot.view.Adapter.ListButtonAdapter.pos;public class ReceivedBtnHolder extends RecyclerView.ViewHolder implements HideButtonsInterface {    TextView messageText;    TextView timeText;    RecyclerView btnRecylcerView;    RecyclerView.Adapter mAdapter;    RecyclerView.LayoutManager layoutManager;    Activity mContext;    LinearLayout messageLayout;    List<String> tempListBtn=new ArrayList<>();    List<String> tempListBtnValue =new ArrayList<>();    List<String> expandTextList=new ArrayList<>();    List<String> expandValue=new ArrayList<>();    List<String> showButtons=new ArrayList<>();    List<String> showButtonsValue=new ArrayList<>();    public ReceivedBtnHolder(View itemView,Activity activity) {        super(itemView);        Log.d(TAG, "ReceivedBtnHolder: ");        this.mContext=activity;        timeText = itemView.findViewById(R.id.rcv_time);        messageText = itemView.findViewById(R.id.rcv_text);        messageLayout=itemView.findViewById(R.id.message_text);        btnRecylcerView =itemView.findViewById(R.id.button_list_view);        layoutManager=new LinearLayoutManager(mContext);        btnRecylcerView.setHasFixedSize(true);        btnRecylcerView.setLayoutManager(layoutManager);    }    public void bind(UserMessage message){        messageText.setText(message.getMessage());        Log.d(TAG, "bind: "+message.getMessage());        Log.d(TAG, "bind: "+message.getTime());        timeText.setText(message.getTime());        for(int i=0;i<message.getBtnText().size();i++)        {            Log.d(TAG, "bind: "+message.getBtnText().get(i));            tempListBtn.add(message.getBtnText().get(i));            tempListBtnValue.add(message.getBtnValue().get(i));        }            mAdapter = new ListButtonAdapter(mContext, message.getBtnText(), message.getBtnValue());        //INITIALIZE        ((ListButtonAdapter) mAdapter).setCi(ReceivedBtnHolder.this);        btnRecylcerView.setAdapter(mAdapter);       // viewBtnRecyclerView.setAlpha(1);    }    public void Hide() {        btnRecylcerView.setEnabled(false);        btnRecylcerView.setAlpha((float) 0.5);        Log.d(TAG, "Hide: ");      //  buttons.addAll(buttons);       // buttons.removeAll(buttons);    }}