package tcs.ril.storebot.view.Adapter;import android.support.v7.widget.RecyclerView;import android.util.Log;import android.view.View;import android.widget.ImageView;import android.widget.TextView;import com.squareup.picasso.Picasso;import tcs.ril.storebot.R;import tcs.ril.storebot.model.UserMessage;import tcs.ril.storebot.view.Other.CircleTransform;import static android.support.constraint.Constraints.TAG;public class SentMessageHolder extends RecyclerView.ViewHolder {    TextView messageText, timeText;    ImageView profileImage;    SentMessageHolder(View itemView) {        super(itemView);        Log.d(TAG, "SentMessageHolder: ");        messageText = itemView.findViewById(R.id.send_text);        timeText = itemView.findViewById(R.id.send_time);        profileImage=itemView.findViewById(R.id.profile_img);    }    void bind(UserMessage message) {        messageText.setText(message.getMessage());        timeText.setText(message.getTime());        Picasso.get()                .load(message.getProfileURL())                .transform(new CircleTransform())                .centerCrop()                .fit()                .into(profileImage);    }}