package tcs.ril.storebot.view.Adapter;import android.content.Context;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.support.v4.view.PagerAdapter;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import java.util.List;import tcs.ril.storebot.R;import tcs.ril.storebot.model.HelpPagerData;import tcs.ril.storebot.view.Activity.HelpActivity;public class ViewPagerAdapter extends PagerAdapter {    private Context mContext;    private List<HelpPagerData> helpPagerDataList;    LayoutInflater layoutInflater;    public ViewPagerAdapter(Context context, List<HelpPagerData> helpPagerData) {        this.mContext=context;        this.helpPagerDataList=helpPagerData;        layoutInflater=LayoutInflater.from(context);    }    @Override    public void destroyItem(@NonNull ViewGroup container, int position, @NonNull Object object) {        container.removeView((View)object);    }    @NonNull    @Override    public Object instantiateItem(@NonNull ViewGroup container, int position) {        View view=layoutInflater.inflate(R.layout.help_pager_item,container,false);        TextView heading=view.findViewById(R.id.title);        RecyclerView recyclerView=view.findViewById(R.id.help_pager_bullet_rv);        RecyclerView.Adapter bulletAdapter;        heading.setText(helpPagerDataList.get(position).getHeading());        bulletAdapter=new HelpPagerBulletPointsAdpater(mContext,helpPagerDataList.get(position));        RecyclerView.LayoutManager layoutManager;        layoutManager=new LinearLayoutManager(mContext);        recyclerView.setLayoutManager(layoutManager);        recyclerView.setAdapter(bulletAdapter);        container.addView(view);        return view;    }    @Override    public int getCount() {        return helpPagerDataList.size();    }    @Override    public boolean isViewFromObject(@NonNull View view, @NonNull Object o) {        return view==o;    }    @Nullable    @Override    public CharSequence getPageTitle(int position) {        return super.getPageTitle(position);    }}